import React from 'react';
import { View, Text, StyleSheet, Button } from 'react-native';
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler';
import { useContext, useEffect, useState } from 'react';
import { Context } from '../context/FeedListContext';
import { FontAwesome } from '@expo/vector-icons';

const IndexScreen = ({ navigation }) => {
    const { state, deleteFeed, restoreState, deleteAll, } = useContext(Context);

    
    return (
        <>
            <Button color="red" title="Apagar tudo" onPress={deleteAll}/>
            <FlatList
                data={state.getFeeds}
                keyExtractor={(saved_feed) => saved_feed.urlFeed}
                renderItem={({ item }) => {
                    return (
                        <View>
                            <View style={styles.row}>
                                <Text style={styles.title}>{item.titulo}</Text>
                                <View style={styles.row}>
                                    <FontAwesome style={styles.add} name='pencil' size={24} color='black' 
                                        onPress={() => navigation.navigate('Show', { id: item.urlFeed })} />
                                    <FontAwesome style={styles.delete} name='trash-o' size={30} color='black' 
                                        onPress={() => deleteFeed(item.urlFeed) } />
                                </View>
                                
                                
                            </View>
                        </View>
                    ); 
                }}
            />
        </>
    );
};

IndexScreen.navigationOptions = ({ navigation }) => {
    return {
        headerRight: () => (
            <FontAwesome.Button style={styles.add} name='plus-circle' size={32} color='black' 
                onPress={() => navigation.navigate('Add')} />

        )
    };
};

const styles = StyleSheet.create({
    row: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingVertical: 20,
        paddingHorizontal: 10,
        borderTopWidth: 1,
        borderColor: 'gray'
    },
    title: {
        fontSize: 18
    },
    add: {
        borderRadius: 5,
        padding: 10,
    },
    delete:{
        borderRadius: 5,
        padding: 10,
    }, 
});

export default IndexScreen;
