import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import { Card, Button, FAB, Header } from 'react-native-elements';
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler';
import { useContext, useEffect } from 'react';
import { Context } from '../context/FeedListContext';
import { FontAwesome } from '@expo/vector-icons';
import color from 'color';

const IndexScreen = ({ navigation }) => {
    const { state, deleteFeed, restoreState, deleteAll } = useContext(Context);

    //Limpa os dados do estado assim que a aplicação é iniciada 
    useEffect(() => {
        restoreState();
    }, []);

    
    return (
        <>
            <Header
                placement="left"
                leftComponent={{ icon: 'menu', color: '#fff' }}
                centerComponent={{ text: 'MY TITLE', style: { color: '#fff' } }}
                rightComponent={{ icon: 'plus-circle', color: '#fff' }}
                color= '#4527a0'
            />
            <Card>
                <Card.Title> <FontAwesome name="rss" style={styles.icons} color='black' size={24} />
                    RSS Feed React</Card.Title>
                <Card.FeaturedSubtitle>Todos os seus feeds em um só lugar</Card.FeaturedSubtitle>
                <Card.Divider/>
                <Button icon={<FontAwesome name="trash-o" size={24} style={styles.icons} />} type="clear" 
                buttonStyle={styles.button} title='APAGAR TUDO' onPress={deleteAll} />
            
                <FlatList
                    data={state}
                    keyExtractor={(feed) => feed.urlFeed}
                    renderItem={({ item }) => {
                        return (
                            <TouchableOpacity onPress={() => navigation.navigate('Show', { id: item.urlFeed })}>
                                <View style={styles.row}>
                                    <Text style={styles.title}>{item.titulo}</Text>
                                    <View style={styles.row}>
                                        <FontAwesome style={styles.icons} name='pencil' size={24} color='black' 
                                            onPress={() => navigation.navigate('Show', { id: item.urlFeed })} />
                                        <FontAwesome style={styles.icons} name='trash-o' size={24} color='black' 
                                            onPress={() => deleteFeed(item.urlFeed) } />
                                    </View>
                                </View>
                            </TouchableOpacity>
                        ); 
                    }}
                />
            </Card>
            <FAB icon={<FontAwesome style={styles.icons} name='plus-circle' size={32} 
            color='black' onPress={() => navigation.navigate('Add')}/>} placement='right' color='#714cfe' size='small'/>
    </>
    );
};


const styles = StyleSheet.create({
    row: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        paddingVertical: 20,
        paddingHorizontal: 10,
        borderTopWidth: 1,
        borderColor: 'gray'
    },
    title: {
        fontSize: 18, 
        padding: 5,
    },
    icons: {
        borderRadius: 5,
        padding: 5,
    },
    button: {
        color: 'red',
        textDecorationColor: 'black'
    }
});

export default IndexScreen;
